#!/usr/bin/env node
import fs from "fs/promises";
const sourcePath = process.argv[2];
const destinationPath = process.argv[3];

async function copyImage(src, dest) {
  try {
    await fs.copyFile(src, dest);
    console.log(`Image copied from ${src} to ${dest}`);
  } catch (error) {
    console.error(`Error copying image: ${error.message}`);
  }
}

if (!sourcePath || !destinationPath) {
  console.error("Usage: copyImage <sourcePath> <destinationPath>");
  process.exit(1);
}

copyImage(sourcePath, destinationPath);
